import React, {useState, useEffect} from 'react';
import PropTypes from 'prop-types';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { atvCompany, getCompanyById } from '../../../../store/companyReducer';

import { makeStyles } from '@material-ui/core/styles';
import Switch from "@material-ui/core/Switch";
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const useStyles = makeStyles(() =>({
  button: {
    marginTop: '25px',
  },
  formControl: {
    padding: '15px 0'
  },
  buttonAtv: {
    margin: '15px 0'
  }, 
  section: {
    border: '1px solid black',
    background: 'red',
  },
  item: {
    border: '1px solid black',
    background: 'green',
  }
}));

const Menu = (props) => {
  const classes = useStyles();
  const { companyById } = props;
  const [ menu, setMenu ] = useState({
    check: companyById.menu
  });

  useEffect(() => {
    setMenu({check: companyById.menu});
  },[companyById.menu])

  const atvMenu = (companyById) => {
    if(companyById.menu){
      companyById.menu = false;
      setMenu({check: companyById.menu});
      return props.atvCompany(companyById);
    }else{
      companyById.menu = true;
      setMenu({check: companyById.menu});
      return props.atvCompany(companyById);
    }
  }

  const [menuType, setMenuType] = useState(1);

  const handleChange = (event) => {
    setMenuType(event.target.value);
  };

  const initial = [
    {
      id : 'id-1',
      content: 'Quote 1'
    },
    {
      id : 'id-2',
      content: 'Quote 2'
    },
    {
      id : 'id-3',
      content: 'Quote 3'
    },
    {
      id : 'id-4',
      content: 'Quote 4'
    },
  ];

  const grid = 8;
  const reorder = (list, startIndex, endIndex) => {
    const result = Array.from(list);
    const [removed] = result.splice(startIndex, 1);
    result.splice(endIndex, 0, removed);

    return result;
  };  

  function Quote({ quote, index }) {
    return (
      <Draggable draggableId={quote.id} index={index}>
        {provided => (
          <QuoteItem
            ref={provided.innerRef}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
          >
            {quote.content}
          </QuoteItem>
        )}
      </Draggable>
    );
  }

  const QuoteList = React.memo(function QuoteList({ quotes }) {
    return quotes.map((quote: QuoteType, index: number) => (
      <Quote quote={quote} index={index} key={quote.id} />
    ));
  });

  const [state, setState] = useState({ quotes: initial });
  function onDragEnd(result) {
    if (!result.destination) {
      return;
    }
    if (result.destination.index === result.source.index) {
      return;
    }
    const quotes = reorder(
      state.quotes,
      result.source.index,
      result.destination.index
    );
    setState({ quotes });
  }

  return(
    <div>
      <Typography variant="h5" component="h2">Card치pio / Cat치logo</Typography>
      <Grid container>
        <Grid item className={classes.buttonAtv}>
          <FormGroup row>
          <FormControlLabel
            control={
              <Switch
                checked={menu.check? menu.check : false}
                onClick={()=>atvMenu(companyById)}
                name="checkedA"
                color="primary"
              />
            }
            label="Ativar/Desativar"
          />
          </FormGroup>
        </Grid>
      </Grid>
      <FormControl className={classes.formControl}>
        <FormHelperText>Tipo:</FormHelperText>
        <Select
          value={menuType}
          onChange={handleChange}
          displayEmpty
          className={classes.selectEmpty}
          inputProps={{ 'aria-label': 'Without label' }}
        >
          <MenuItem value={1}>Card치pio</MenuItem>
          <MenuItem value={2}>Cat치logo</MenuItem>
        </Select>
      </FormControl>
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="list">
          {provided => (
            <div ref={provided.innerRef} {...provided.droppableProps}>
              <QuoteList quotes={state.quotes} />
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

Menu.prototypes = {
  companyById: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
  companyById: state.company.companyById,
});

const mapsDispatchToProps = dispatch => 
  bindActionCreators({atvCompany, getCompanyById}, dispatch);

export default connect(mapStateToProps, mapsDispatchToProps)(Menu);
