import api from '../services/api';
import history from '../services/history';

const ACTIONS = {
  LIST: 'LIST',
}
const INITIAL_STATE = {
  suggest:[],
}
export const admReducer = (state = INITIAL_STATE, action) => {
  switch(action.type){
    case ACTIONS.LIST:
      return {...state, suggest: action.suggest}
    default:
      return state;
  }
}
export const listSuggest = (page, nOfItems) => {
  return dispatch => {
    api.get(`/adm/suggest?page=${page}&limit=${nOfItems}`)
    .then(Response => {
      const { docs, ...infos} = Response.data;
      dispatch({
          type: ACTIONS.LIST,
          suggest: docs,
          infos: infos,
      });
    })
    .catch(error => {
      console.log(error);
    });
  }
}
