import React, {useState, useEffect} from 'react';
import PropTypes from 'prop-types';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { atvCompany, getCompanyById } from '../../../../store/companyReducer';

import { makeStyles } from '@material-ui/core/styles';
import Switch from "@material-ui/core/Switch";
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles(() =>({
  button: {
    marginTop: '25px',
  },
  formControl: {
    padding: '15px 0'
  },
  buttonAtv: {
    margin: '15px 0'
  },
}));

const Menu = (props) => {
  const classes = useStyles();
  const { companyById, menuById } = props;
  const [ menu, setMenu ] = useState({
    check: companyById.menu
  });

  useEffect(() => {
    setMenu({check: companyById.menu});
  },[companyById.menu])

  const atvMenu = (companyById) => {
    if(companyById.menu){
      companyById.menu = false;
      setMenu({check: companyById.menu});
      return props.atvCompany(companyById);
    }else{
      companyById.menu = true;
      setMenu({check: companyById.menu});
      return props.atvCompany(companyById);
    }
  }

  const [menuType, setMenuType] = useState(1);

  const handleChange = (event) => {
    setMenuType(event.target.value);
  };

  return(
    <div>
      <Typography variant="h5" component="h2">Cardápio / Catálogo</Typography>
      <Grid container>
        <Grid item className={classes.buttonAtv}>
          <FormGroup row>
          <FormControlLabel
            control={
              <Switch
                checked={menu.check? menu.check : false}
                onClick={()=>atvMenu(companyById)}
                name="checkedA"
                color="primary"
              />
            }
            label="Ativar/Desativar"
          />
          </FormGroup>
        </Grid>
      </Grid>
      <FormControl className={classes.formControl}>
        <FormHelperText>Tipo:</FormHelperText>
        <Select
          value={menuType}
          onChange={handleChange}
          displayEmpty
          className={classes.selectEmpty}
          inputProps={{ 'aria-label': 'Without label' }}
        >
          <MenuItem value={1}>Cardápio</MenuItem>
          <MenuItem value={2}>Catálogo</MenuItem>
        </Select>
      </FormControl>

      <Paper>
        <TableContainer>
          <Table stickyHeader aria-label="sticky table">
            <TableHead>
              <TableRow>
                <TableCell align='center'>Mesa</TableCell>
                <TableCell align='center'>Hora</TableCell>
                <TableCell align='center'></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              { calls.map( call => {
                return (
                  <TableRow hover key={call.createdAt} role="checkbox" tabIndex={-1}>
                    <TableCell align='center'>{call.table}</TableCell>
                    <TableCell align='center'>{format(parseISO(call.createdAt), 'dd/MM/yyyy HH:mm', {timeZone: 'America/Sao_Paulo'} )}</TableCell>
                    <TableCell align='center'>
                      <Button className={classes.button} onClick={()=>props.destroyCall(call._id)} variant="contained" color="primary">
                        Atendida
                      </Button>
                    </TableCell>
                  </TableRow>
                )
              })}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </div>
  );
}

Menu.prototypes = {
  companyById: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
  companyById: state.company.companyById,
  menuById: state.menu.menuById,
});

const mapsDispatchToProps = dispatch => 
  bindActionCreators({atvCompany, getCompanyById}, dispatch);

export default connect(mapStateToProps, mapsDispatchToProps)(Menu);
