import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { addCall } from '../../../store/clientReducer';
import { bindActionCreators } from 'redux';

const AtentionClient = (props) => {
  const { infos } = props;
  const [ idCompany ] = useState(window.location.href.split('?')[1]);
  const [ idTable ] = useState(window.location.href.split('table=')[1]);
  const [ call ] = useState({
    table: idTable,
    companyId: idCompany,
  })

  useEffect(() => {
    props.addCall(call);
  },[])

  return(
    <div>
      <h2>Chamado enviado!</h2>
      <p>Chegaremos assim que possível.</p>
      <p>Enquanto isso você pode ver nosso cardápio online no botão abaixo:</p>
      <div>
        <Link to={`/client/cardapio/?${infos._id}`}>Ver cadápio online</Link>
      </div>
    </div>
  );
}

AtentionClient.prototypes = {
  infos: PropTypes.array.isRequired
};

const mapDispatchToProps = dispatch =>
  bindActionCreators({addCall}, dispatch);

const mapStateToProps = state => ({
  infos: state.client.infos
});

export default connect(mapStateToProps, mapDispatchToProps)(AtentionClient);
