import api from '../services/api';

const ACTIONS = {
  LIST_MENU: 'LISTMENU',
  ATIV_MENU: 'ATIVMENU'
}
const INITIAL_STATE = {
  menu: [],
}
export const menuReducer = (state = INITIAL_STATE, action) => {
  switch(action.type){
    case ACTIONS.LIST_MENU:
      return {...state, menu: action.menu, infosMenu: action.infosMenu}
    default:
      return state;
  }
}
//LISTAR MENU
export const listSuggest = (idCompany) => {
  return dispatch => {
    api.get(`/adm/menu/${idCompany}`)
    .then(Response => {
      const { docs } = Response.data;
      dispatch({
        type: ACTIONS.LIST_MENU,
        menu: docs,
      })
    })
    .catch(error => {
      console.log(error);
    });
  }
}
//ATIVAR MENU
export const favorite = (menu) => {
  return dispatch => {
    api.put(`/adm/suggest/${menu._id}`, menu )
    .then(Response => {
      dispatch({
        type: ACTIONS.ATIV_MENU,
      })
    })
  }
}
