import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { bindActionCreators } from 'redux';
import { submitSuggest } from '../../../store/clientReducer'
import { connect } from 'react-redux';
import NumberFormat from 'react-number-format';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import Checkbox from '@material-ui/core/Checkbox';
import Rating from '@material-ui/lab/Rating';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import CssBaseline from '@material-ui/core/CssBaseline';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import Star from '@material-ui/icons/Star';

const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
    },
  },
}));

const NumberFormatCustom = (props) => {
  const { inputRef, onChange, ...other } = props;

  return (
    <NumberFormat
      {...other}
      getInputRef={inputRef}
      onValueChange={(values) => {
        onChange({
          target: {
            name: props.name,
            value: values.value,
          },
        });
      }}
      isNumericString
      format='(##) #########'
    />
  );
}

NumberFormatCustom.propTypes = {
  inputRef: PropTypes.func.isRequired,
  name: PropTypes.string.isRequired,
  onChange: PropTypes.func.isRequired,
};

const FormClient = () => {
  const classes = useStyles();
  const [checked, setChecked] = useState(false);
  const [values, setValues] = useState({
    name: '',
    phone: '',
    stars: ''
  });

  const handleChange = (event) => {
    setValues({...values, [event.target.name]: event.target.value });
    console.log(values);
  };
  const checkChange = (event) => {
    setValues({name: '', phone: ''});
    setChecked(event.target.checked);
  };

  return (
    <div className={classes.root}>
      <FormControl className={classes.center}>
        <TextField 
          label="Nome"
          disabled={checked}
          value={values.name} 
          onChange={handleChange}
          name="name"
          id="standard-basic" 
        />
        <TextField
          label="Telefone"
          disabled={checked}
          value={values.phone}
          onChange={handleChange}
          name="phone"
          id="formatted-numberformat-input"
          InputProps={{inputComponent: NumberFormatCustom}}
        />
        <FormControlLabel
          label="Desejo nÃ£o me identificar"
          control={<Checkbox checked={values.checkedA} 
          onChange={checkChange} 
          name="checkedA" />}
        />
        <Box component="fieldset" mb={3} borderColor="transparent">
          <Typography component="legend">Toque para classificar</Typography>
          <Rating
            name="stars"
            onChange={handleChange}
            defaultValue={0}
            icon={<Star fontSize="large" />}
            emptyIcon={<StarBorderIcon fontSize="large" />}
          />
        </Box>
      </FormControl>
    </div>
  );
}

FormClient.prototypes = {
  infos: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
  infos: state.client.infos
});

const mapDispatchToProps = dispatch =>
  bindActionCreators({submitSuggest}, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(FormClient);
