import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import ScratchCard from 'react-scratchcard';

import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: 'auto',
      width: '100%',
    },
  },
  centerText: {
    textAlign: 'center',
  },
  okText: {
    textAlign: 'center',
    margin: '15px'
  },
  button: {
    marginTop: '15px'
  },
  photo: {
    margin: '0 auto',
    marginBottom: '10px',
    marginTop: '20px',
    width: 128,
    height: 128,
  },
  touch: {
    touchAction: 'none',
  }
}));

const ConfirmClient = (props) => {
  const classes = useStyles();
  const { infos } = props;

  const settings = {
    width: 240,
    height: 180,
    image: "/assets/box.jpg",
    finishPercent: 50,
    
  };
  const Example = () =>
  <ScratchCard {...settings}>
    Congratulations! You WON!
  </ScratchCard>;

  return (
    <Grid container className={classes.root}>
      <Typography className={classes.okText} variant="h5">Obrigado pela sua opinião!</Typography>
      <Example/>
      <Typography className={classes.centerText} >Parabéns vocês ganhou uma caipirinha na sua proxima visita!</Typography>
      <Button className={classes.button} component={Link} to={`/client/?${infos._id}?table=${infos.idTable? infos.idTable: 0}`} variant="contained" color="primary">
        Voltar ao início
      </Button>
    </Grid>
  );
}

ConfirmClient.prototypes = {
  infos: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
  infos: state.client.infos
});

export default connect(mapStateToProps)(ConfirmClient);
