import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { bindActionCreators } from 'redux';
import { submitSuggest } from '../../../store/clientReducer'
import { connect } from 'react-redux';
import NumberFormat from 'react-number-format';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import Checkbox from '@material-ui/core/Checkbox';

const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '25ch'
    },
  },
}));

const NumberFormatCustom = (props) => {
  const { inputRef, onChange, ...other } = props;

  return (
    <NumberFormat
      {...other}
      getInputRef={inputRef}
      onValueChange={(values) => {
        onChange({
          target: {
            name: props.name,
            value: values.value,
          },
        });
      }}
      isNumericString
      format='(##) #########'
    />
  );
}

NumberFormatCustom.propTypes = {
  inputRef: PropTypes.func.isRequired,
  name: PropTypes.string.isRequired,
  onChange: PropTypes.func.isRequired,
};

const FormClient = () => {
  const classes = useStyles();
  const [checked, setChecked] = useState(false);
  const [values, setValues] = useState({
    textmask: '',
    numberformat: ''
  });

  const handleChange = (event) => {
    setValues({...values, [event.target.name]: event.target.value});
  };
  const checkChange = (event) => {
    setValues({numberformat: ''});
    setValues({textmask: '', })
    setChecked(event.target.checked);
  };

  return (
    <div className={classes.root}>
      <FormControl>
        <TextField disabled={checked} error={false} id="standard-basic" label="Nome" />
        <TextField
          label="Telefone"
          disabled={checked}
          value={values.numberformat}
          onChange={handleChange}
          name="numberformat"
          id="formatted-numberformat-input"
          InputProps={{
            inputComponent: NumberFormatCustom,
          }}
        />
        <FormControlLabel
        control={<Checkbox checked={values.checkedA} onChange={checkChange} name="checkedA" />}
        label="Desejo nÃ£o me identificar"
        />
      </FormControl>
    </div>
  );
}

FormClient.prototypes = {
  infos: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
  infos: state.client.infos
});

const mapDispatchToProps = dispatch =>
  bindActionCreators({submitSuggest}, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(FormClient);
