import React, { useState } from 'react';
import RoutesUser from './routesUser';
import { Link } from 'react-router-dom';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { logout } from '../../store/authReducer';
import clsx from 'clsx';

import { makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import AppBar from '@material-ui/core/AppBar';
import CssBaseline from '@material-ui/core/CssBaseline';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import NotificationsIcon from '@material-ui/icons/Notifications';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import SettingsIcon from '@material-ui/icons/Settings';
import IconButton from '@material-ui/core/IconButton';
import Badge from '@material-ui/core/Badge';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import Avatar from '@material-ui/core/Avatar';
import Rating from '@material-ui/lab/Rating';
import MenuIcon from '@material-ui/icons/Menu';
import Brightness4Icon from '@material-ui/icons/Brightness4';

const drawerWidth = 210;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: 'none',
  },
  title: {
    flexGrow: 1,
    textDecoration: 'none',
  },
  drawerPaper: {
    position: 'relative',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: '100vh',
    overflow: 'auto',
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(2),
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
  },
  fixedHeight: {
    height: 240,
  },
  icon: {
    textDecoration: 'none',
    color: 'white',
  },
  menu: {
    display: 'none'
  },
  profile: {
    paddingRight: 17
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerContainer: {
    overflow: 'auto',
  },
  stars: {
    textAlign: 'center'
  },
  card: {
    maxWidth: 200,
    textAlign: 'center',
    margin: 'auto',
    marginTop: '5px'
  },
  avatar: {
    backgroundColor: '#dd2c00',
  },
  menu: {
    display: 'flex',
  },
  item1: {
    display: 'flex',
    justifyContent: 'flex-start'
  },
  item2: {
    display: 'flex',
    justifyContent: 'flex-end'
  }
}));

const User = (props) => {
  const classes = useStyles();
  const { infos } = props;

  const [menu, setMenu] = useState(true);
  const [open, setOpen] = useState(true);
  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const handleDrawerClose = () => {
    setOpen(false);
  };

  return (
    <div className={classes.root}>
      <CssBaseline />

      <AppBar position="absolute" className={clsx(classes.appBar, open && classes.appBarShift)}>
        <Toolbar className={classes.toolbar}>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            className={clsx(classes.menuButton, open && classes.menuButtonHidden)}>
            <MenuIcon />
          </IconButton>
          <Typography component={Link} to='/user' variant="h6" color="inherit" noWrap className={classes.title}>
              SuggestInBox
          </Typography>
            <div className={classes.profile}>Dr. Randel</div>
          <IconButton color="inherit">
            <Brightness4Icon/>
          </IconButton>
          <IconButton color="inherit">
            <Badge badgeContent={2} color="secondary">
              <NotificationsIcon />
            </Badge>
          </IconButton>
          <IconButton component={Link} to='/user/setup' color="inherit">
            <SettingsIcon/>
          </IconButton>
          <IconButton onClick={props.logout} color="inherit">
            <ExitToAppIcon />
          </IconButton>
        </Toolbar>
      </AppBar>

      <Drawer
        className={classes.drawer}
        variant="permanent"
        classes={{paper: classes.drawerPaper}}>
        <Toolbar />
        <div className={classes.drawerContainer}>
          <List>
            <Card className={classes.card}>
              <CardHeader
                avatar={<Avatar aria-label="recipe" className={classes.avatar}>S</Avatar>}
                title="Skinão Bar"
              />
              <CardContent className={classes.stars}>
                <Typography variant="h3" color="textSecondary" component="p">
                  4,5
                </Typography>
                <Rating name="read-only" precision={0.1} value={4.5} readOnly />
              </CardContent>
            </Card>
            <ListItem button={true} component={Link} to='/user'>
              <ListItemIcon><InboxIcon /></ListItemIcon>
              <ListItemText primary='Sugestões' />
            </ListItem>
            <ListItem button={true} component={Link} to='/user/promo'>
              <ListItemIcon><InboxIcon /></ListItemIcon>
              <ListItemText primary='Promoções' />
            </ListItem>
            <ListItem button={true} component={Link} to='/user/setup'>
              <ListItemIcon><InboxIcon /></ListItemIcon>
              <ListItemText primary="Configurações" />
            </ListItem>
            <ListItem button={true} component={Link} to='/user/finances'>
              <ListItemIcon><InboxIcon /></ListItemIcon>
              <ListItemText primary='Financeiro' />
            </ListItem>
          </List>
          <Divider />
        </div>
      </Drawer>
      <main className={classes.content}>
        <Toolbar />
        <RoutesUser/>
      </main>
    </div>
  );
}

User.prototypes = {
  infos: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
  infos: state.client.infos,
});

const mapDispatchToProps = dispatch =>
  bindActionCreators({logout}, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(User);
