import React, { useState, useEffect} from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import RoutesClient from './routesClient';
import { getInfo } from '../../store/clientReducer';
import history from '../../services/history';

import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles((theme) => ({
  header:{
    background: '#FFB701',
    textAlign: 'center',
  },
  img:{
    padding: '10px'
  },
  link:{
    margin: 'auto'
  },
  labelLink:{
    padding: '10px',
    fontFamily: ''
  },
  appBar: {
    top: 'auto',
    bottom: 0,
  },
  grow: {
    flexGrow: 1,
  }
}));

const theme = createMuiTheme({
  palette: {
    primary: {
      main: '#FED200',
    }
  },
});

const Client = (props) => {
  const { infos } = props;
  const [ idUrl ] = useState(window.location.href.split('/?')[1]);
  const classes = useStyles();

  useEffect(() => {
    if(idUrl){
      props.getInfo(idUrl);
    }else{
      history.push('/');
    }
  },[]);

  return(
    <MuiThemeProvider theme={theme}>
      <div className={classes.header}>
        <img className={classes.img} src='/assets/logo_palpitebox.png' alt='logo'/>
      </div>
      <AppBar position="static">
        <Toolbar color="primary">
          <Typography variant="button" className={classes.link}>
            <Button component={Link} to={`/client/?${infos._id}`}>INÍCIO</Button>
            <Button component={Link} to={`/client/sobrenos/?${infos._id}`}>SOBRE</Button>
          </Typography>
        </Toolbar>
      </AppBar>
      <div>
        <RoutesClient/>
      </div>
      <footer>
        <AppBar position="fixed" color="primary" className={classes.appBar}>
          <Toolbar>
          <div className={classes.grow} />
          <div>
            <p>Colocar Suggest na sua empresa, <Link to='/'>clique aqui!</Link></p>
            <p>Suggest In Box - Todos os direitos reservados.</p>
            <p>© 2020 - Copyright</p>
          </div>
          </Toolbar>
        </AppBar>
      </footer>
    </MuiThemeProvider>
  );
}

Client.prototypes = {
  infos: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
  infos: state.client.infos
});

const mapDispatchToProps = dispatch =>
  bindActionCreators({getInfo}, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Client);
