import api from '../services/api';
import history from '../services/history';

const ACTIONS = {
  LIST_CALLS: 'LISTCALLS',
  BY_ID_CALL: 'BYIDCALL',
  ADD_CALLS: 'ADDCALLS',
  UPDATE_CALL: 'UPDATECALL',
}
const INITIAL_STATE = {
  calls: [],
  infosCalls: [],
  callById: [],
}
export const callReducer = (state = INITIAL_STATE, action) => {
  switch(action.type){
    case ACTIONS.LIST_CALLS:
      return {...state, calls: action.calls, infosCalls: action.infosCalls}
    case ACTIONS.ADD_CALLS:
      return {...state}
    case ACTIONS.BY_ID_CALL:
      return {...state, callById: action.callById}
    case ACTIONS.UPDATE_CALL:
      return state;
    default:
      return state;
  }
}
//LISTAR COMPANIAS
export const listCalls = (page, nOfItems) => {
  return dispatch => {
    api.get(`/adm/company?page=${page}&limit=${nOfItems}`)
    .then(Response => {
      const { docs, infos } = Response.data;
      dispatch({
        type: ACTIONS.LIST_COMPANIES,
        companies: docs,
        infosCompanies: infos,
      })
    })
    .catch(error => {
      console.log(error);
    });
  }
}
//ADICIONAR COMPANY
export const addCall = (company, id) => {
  return dispatch => {
    api.post('/adm/company', company)
    .then(Response => {
      dispatch({
        type: ACTIONS.ADD_COMPANY,
      });
    }, history.push(`/user`))
    .catch(error => {
      console.log(error);
    });
  }
}
//GET BY ID COMPANY
export const getCallById = (id) => {
  return dispatch => {
    api.get(`/adm/company/${id}`)
    .then(Response => {
      dispatch({
        type: ACTIONS.BY_ID_COMPANY,
        companyById: Response.data,
      })
    })
    .catch(error => {
      console.log(error);
    })
  }
}
//UPDATE COMPANY
export const updateCall = (company, id, idUser) => {
  return dispatch => {
    api.put(`/adm/company/${id}`, company)
    .then(Response => {
      dispatch({
        type: ACTIONS.UPDATE_COMPANY,
      })
    }, history.push(`/suggest/?${idUser}/?${id}?page=1&limit=25`))
    .catch(error => {
      console.log(error)
    })
  }
}
