import React from 'react';


const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: 'auto',
      width: '100%',
    },
  },
  photo: {
    margin: '0 auto',
    marginBottom: '10px',
    marginTop: '20px',
    width: 128,
    height: 128,
  },
  center: {
    textAlign: 'center',
    margin: 'auto',
    marginBottom: '20px',
  },
  button: {
    marginBottom: "20px"
  },
  hide: {
    display: 'none'
  }
}));

const MenuClient = (props) =>{
  const { menu } = props;
  const classes = useStyles();

  return(
    <div>
      <Paper>
        <TableContainer>
          <Table stickyHeader aria-label="sticky table">
            <TableHead>
              <TableRow>
                <TableCell align='center'>Mesa</TableCell>
                <TableCell align='center'>Hora</TableCell>
                <TableCell align='center'></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              { calls.map( call => {
                return (
                  <TableRow hover key={call.createdAt} role="checkbox" tabIndex={-1}>
                    <TableCell align='center'>{call.table}</TableCell>
                    <TableCell align='center'>{format(parseISO(call.createdAt), 'dd/MM/yyyy HH:mm', {timeZone: 'America/Sao_Paulo'} )}</TableCell>
                    <TableCell align='center'>
                      <Button className={classes.button} onClick={()=>props.destroyCall(call._id)} variant="contained" color="primary">
                        Atendida
                      </Button>
                    </TableCell>
                  </TableRow>
                )
              })}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </div>
  );
}

MenuClient.prototypes = {
  infos: PropTypes.array.isRequired,
  idTable: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
  infos: state.client.infos,
  idTable: state.client.idTable,
  menu: state.client.menu
});

const mapDispatchToProps = dispatch =>
  bindActionCreators({getMenu}, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(MenuClient);
