import React from 'react';
import { Link } from 'react-router-dom';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import styles from './index.module.css';

const Login = () => {
  return(
    <Formik
      initialValues={{email: '', password: ''}}
      validationSchema={Yup.object({
        email: Yup.string()
        .required('Email obrigatório'),
      password: Yup.string()
        .required('Senha obrigatória'),
      })}
      onSubmit={(values, { setSubmitting }) => {
        setTimeout(() => {
          alert(JSON.stringify(values, null, 2));
          setSubmitting(false);
        }, 400);
      }}
    >
      <div>
        <header>
          <div>
            <h1 className={styles.logo}><img src='/assets/logo_palpitebox.png' alt='logo'/></h1>
          </div>
        </header>
        <section>
          <div>
            <h3 className={styles.login}>Login</h3>
            <Form>
              <div className={styles.wrap}>
                <label className={styles.label} htmlFor='email'>Email:</label>
                <Field className={styles.input} name='email' type='email'/>
                <ErrorMessage name='email'/>
              </div>
              <div className={styles.wrap}>
                <label className={styles.label} htmlFor='password'>Senha:</label>
                <Field className={styles.input} name='password' type='password'/>
                <ErrorMessage name='password'/>
              </div>
              <div className={styles.wrap}>
                <input name='fogot' type='checkbox'/>
                <label className={styles.labelCheckbox}>Lembra-me</label>
                <label className={styles.labelFogot} name='fogot'><Link>Esqueci minha senha</Link></label>
              </div>
              <div>
                <button className={styles.link} type='submit'>Entrar</button>
              </div>
            </Form>
          </div>
        </section>
        <footer className={styles.footer}>
          <div>
            <p>Colocar Suggest na sua empresa, <Link to='/'>clique aqui!</Link></p>
            <p>Suggest In Box - Todos os direitos reservados.</p>
            <p>© 2020 - Copyright</p>
          </div>
        </footer>
      </div>
    </Formik>
  );
}

export default Login;
