import React, {useState, useEffect} from 'react';
import PropTypes from 'prop-types';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { getMenu } from '../../../store/clientReducer';

import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { TableHead } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: 'auto',
      width: '100%',
    },
  },
  photo: {
    margin: '0 auto',
    marginBottom: '10px',
    marginTop: '20px',
    width: 128,
    height: 128,
  },
  center: {
    textAlign: 'center',
    margin: 'auto',
    marginBottom: '20px',
  },
  button: {
    marginBottom: "20px"
  },
  hide: {
    display: 'none'
  }
}));

const MenuClient = (props) =>{
  const classes = useStyles();
  const { menu, sectionMenu, itemMenu } = props;

  const [companyId] = useState(window.location.href.split('?')[1]);

  useEffect(() => {
    props.getMenu(companyId);
  },[])

  return(
    <div>
      <Paper>
        {sectionMenu.map(section => {
          return (
            <TableContainer key={section._id}>
              <Table stickyHeader aria-label="sticky table">
                <TableHead>
                  <TableRow>
                    <TableCell>{section.type}</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  <TableRow>
                    {section.itemMenu.map(item => {
                      return (
                        <TableCell key={item._id}>{item.name}</TableCell>
                      )
                    })}
                  </TableRow>
                </TableBody>
              </Table>
            </TableContainer>
          )})}
      </Paper>
    </div>
  );
}

MenuClient.prototypes = {
  infos: PropTypes.array.isRequired,
  idTable: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
  infos: state.client.infos,
  idTable: state.client.idTable,
  menu: state.client.menu,
  sectionMenu: state.client.sectionMenu,
  itemMenu: state.client.itemMenu
});

const mapDispatchToProps = dispatch =>
  bindActionCreators({getMenu}, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(MenuClient);
